package be.ccfun.day6;

public class Day6 {

	public static void main(String[] args) {
		System.out.println(getMarkerPosition(4, "bvwbjplbgvbhsrlpgdmjqwftvncz"));
		System.out.println(getMarkerPosition(4, "nppdvjthqldpwncqszvftbrmjlhg"));
		System.out.println(getMarkerPosition(4, "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"));
		System.out.println(getMarkerPosition(4, "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"));
		System.out.println(getMarkerPosition(4, ""));


		System.out.println(getMarkerPosition(14, "bvwbjplbgvbhsrlpgdmjqwftvncz"));
		System.out.println(getMarkerPosition(14, "nppdvjthqldpwncqszvftbrmjlhg"));
		System.out.println(getMarkerPosition(14, "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"));
		System.out.println(getMarkerPosition(14, "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"));
		System.out.println(getMarkerPosition(14, ""));
	}


	public static int getMarkerPosition(int markerSize, String text) {
		int pos = 0;
		while (pos < text.length() - markerSize) {
			String nextMarker = text.substring(pos, pos + markerSize);
			if (isMarker(nextMarker)) {
				return pos + markerSize;
			} else {
				pos++;
			}
		}
		return -1;
	}

	public static boolean isMarker(String text) {
		return text.chars().distinct().count() == text.length();
	}
}
